  if(!empty($xml)) {
    foreach($xml AS $item) {
    	$tmp_return = array();
    	if(
    		isset($item->club_id) && !empty($item->club_id) &&
    		isset($item->name) && !empty($item->name) &&
    		($club_id=='' || $club_id==$item->team_id)
    	) {
    		//echo '<pre>';print_R($item);die;
    		$tmp_return['id'] = (int)$item->team_id;
    		$tmp_return['name'] = (string)$item->name;

    		if(!empty($club_id)) {
    			$tmp_return['fullname'] = (isset($item->fullname) && !empty($item->fullname)) ? (string)$item->fullname : '';
    			$tmp_return['found'] = (isset($item->found) && !empty($item->found)) ? (string)$item->found : '';
    			$tmp_return['clubphoto'] = (isset($item->clubphoto) && !empty($item->clubphoto)) ? (string)$item->clubphoto : '';
    			$tmp_return['president'] = (isset($item->president) && !empty($item->president)) ? (string)$item->president : '';
    			$tmp_return['honor'] = array();
    			$tmp_return['player'] = array();

    			if(isset($item->honor) && !empty($item->honor)) {
    				$tmp_honor = explode(',', (string)$item->honor);
    				foreach($tmp_honor AS $honor) {
    					if(!empty($honor)) {
    						array_push($tmp_return['honor'], trim($honor));
    					}
    				}
    			}

    			if(isset($item->player) && !empty($item->player)) {
    				foreach($item->player AS $playerlist) {
    					foreach($playerlist AS $player) {
    						if(isset($player->name) && !empty($player->name)) {
    							$tmp_return['player'][] = array(
    								'name' => (string) $player->name,
    								'position' => isset($player->position) && !empty($player->position) ? (string) $player->position :"-",
    								'photo' => isset($player->photo) && !empty($player->photo) ? (string) $player->photo :"-"
    							);
    						}
    					}
    				}
    			}
    		}

    		$return['total']++;
    		$return['club_list']['data'][] = $tmp_return;
    	}
